import React, { useState } from "react";

import Card from "../UI/Card";
import ExpensesFilter from "./ExpensesFilter";
import ExpensesList from "./ExpensesList";
import ExpensesChart from "./ExpensesChart";
import "./Expenses.css";

function Expenses(props) {
  const [year, setYear] = useState(2021);

  const getYearHandler = (selectYear) => {
    setYear(selectYear);
  };
  console.log(props.expenses);

  const [filteredExpenses, setFilteredExpesenses] = useState(props.expenses);

  const filteredExpense = () => {
    setFilteredExpesenses(props.expenses.filter((expense) => {
      return expense.date.getFullYear().toString() === year;
    }))
  };

  return (
    <div>
      <Card className="expense">
        <ExpensesChart expenses={filteredExpense} />
        <ExpensesFilter selectedYear={year} onGetYear={getYearHandler} />
        <ExpensesList items={filteredExpenses} />
      </Card>
    </div>
  );
}

export default Expenses;
